import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Button from '@material-ui/core/Button'
import Divider from '@material-ui/core/Divider'
import IconButton from '@material-ui/core/IconButton'
import FormControl from '@material-ui/core/FormControl'
import Select from '@material-ui/core/Select'
import MenuItem from '@material-ui/core/MenuItem'
import { useMediaQuery } from 'react-responsive'
import { withStyles } from '@material-ui/core/styles'
import { palette } from '../../constants/styles'
import logoIcon from '../../images/logo-full.png'
import menuIcon from '../../images/menu.svg'
import closeIcon from '../../images/close.svg'
import Footer from './Footer'
import Wallet from './Wallet'

var store = require('store')

const options = ["Currency", "Wallet", "Buy Bitcoin", "Education", "Resources", "Sign out"]

const Mobile = ({ children }) => {
  const isMobile = useMediaQuery({ maxWidth: 767 })
  return isMobile ? children : null
}

const Default = ({ children }) => {
  const isNotMobile = useMediaQuery({ minWidth: 768 })
  return isNotMobile ? children : null
}

const styles = {
  appBarContainer: {
    alignItems: 'center',
    background: palette.black[-1],
    display: 'flex',
    justifyContent: 'space-between',
    flexDirection: 'row',
    height: '80px',
    width: '100%'
  },

  button: {
    color: palette.gray[1],
    padding: '0 15px 0 15px',
    textDecoration: 'none'
  },

  container: {
    alignItems: 'center',
    display: 'flex',
    flexDirection: 'column',
  },

  divider: {
    width: '100%'
  },

  footer: {
    width: '100%'
  },

  logoIcon: {
    margin: '0 0 0 30px',
    objectFit: 'contain',
    height: '30px',
  },

  menuOptionsContainer: {
    margin: '0 20px 0 0'
  },

  iconButton: {
    height: '30px',
    width: '30px',
  },

  menuIcon: {
    color: palette.blue[0],
    margin: '0 20px 0 0'
  },

  expandedOption: {
    alignItems: 'center',
    background: palette.black[-1],
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'row',
    height: '60px',
    width: '100vw'
  },

  expandedOptionTextUnselected: {
    color: palette.gray[1],
    cursor: 'pointer',
    fontSize: '16px',
    fontWeight: '700',
    padding: '0 20px 0 20px'
  },

  expandedOptionTextSelected: {
    color: palette.blue[0],
    cursor: 'pointer',
    fontSize: '16px',
    fontWeight: '700',
    padding: '0 20px 0 20px'
  },

  mainContent: {
    alignItems: 'center',
    display: 'flex',
    flexDirection: 'column',
    width: '100%'
  },

  selectedButton: {
    color: palette.blue[0],
    padding: '0 15px 0 15px',
    textDecoration: 'underline'
  }
}

class Home extends Component {

  componentWillMount() {
    if (!store.get('token')) {
      window.location = '/signin'
    }
  }

  state = {
    isExpanded: false,
    selectedOption: options[0],
    currency: 'CAD',
    currencyOption: 1
  }

  handleCurrencyChange = name => event => {
    this.setState({
      currencyOption: event.target.value,
    })
  }

  handleOptionChange = (option) => {
    if (option === "Sign out") {
      store.clearAll()
      window.location = '/signin'
    }
    this.setState({
      selectedOption: option
    })
  }

  handleMenuClick = () => {
    const { isExpanded } = this.state

    this.setState({
      isExpanded: !isExpanded
    })
  }

  render() {
    const { currency, currencyOption, isExpanded, selectedOption } = this.state
    const { classes } = this.props

    return (
      <div>
        {store.get('token') ?

          <div className={classes.container}>
            <div className={classes.appBarContainer}>
              <img src={logoIcon} className={classes.logoIcon} alt="" />
              <Mobile>
                {
                  isExpanded ?

                    < IconButton aria-label="menu" className={classes.menuIcon} onClick={this.handleMenuClick}>
                      <img src={closeIcon} className={classes.iconButton} alt="" />
                    </IconButton >

                    :

                    < IconButton aria-label="menu" className={classes.menuIcon} onClick={this.handleMenuClick}>
                      <img src={menuIcon} className={classes.iconButton} alt="" />
                    </IconButton >
                }

              </Mobile>
              <Default>
                <div className={classes.menuOptionsContainer}>
                  {
                    options.map(function (option, i) {
                      if (i == 0) {
                        return (
                          <FormControl className={classes.formControl}>
                            <Select
                              className={classes.select}
                              value={currencyOption}
                              onChange={this.handleCurrencyChange('currency')}
                            >
                              <MenuItem value={1}>CAD</MenuItem>
                              <MenuItem value={2}>USD</MenuItem>
                              <MenuItem value={3}>EUR</MenuItem>
                            </Select>
                          </FormControl>
                        )
                      } else {
                        return (
                          <Button key={option} className={option === selectedOption ? classes.selectedButton : classes.button} onClick={() => this.handleOptionChange(option)}>
                            {option}
                          </Button>
                        )
                      }

                    }.bind(this))
                  }
                </div>
              </Default>
            </div>
            <Mobile>
              {
                isExpanded ?
                  options.map(function (option, i) {
                    if (i == 0) {
                      return (
                        <Select
                            native
                            value={currency}
                            onChange={this.handleCurrencyChange('currency')}
                            labelWidth={0}
                            inputProps={{
                              name: 'currency',
                              id: 'outlined-currency-native-simple',
                            }}
                          >
                            <option value={10}>CAD</option>
                            <option value={20}>USD</option>
                            <option value={30}>ERU</option>
                          </Select>
                      )
                    } else {
                    return (
                      <div key={option} onClick={() => this.handleOptionChange(option)}>
                        <div className={classes.expandedOption}>
                          <div className={option === selectedOption ? classes.expandedOptionTextSelected : classes.expandedOptionTextUnselected}>
                            {option}
                          </div>
                        </div>
                        <Divider />
                      </div>
                      )
                    }
                  }.bind(this))
                  :
                  <Divider className={classes.divider} />
              }

            </Mobile>
            <Default>
              <Divider className={classes.divider} />
            </Default>

            <div className={classes.mainContent}>
              <Wallet />
            </div>


            <div className={classes.footer}>
              <Footer />
            </div>
          </div>

          :

          <div />
        }
      </div>

    )
  }
}

Home.propTypes = {
  classes: PropTypes.object.isRequired
}

export default withStyles(styles)(Home)
