import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { withStyles } from '@material-ui/core/styles'
import { palette } from '../../constants/styles'

// Styles
const styles = ({
  body: {
    fontSize: '17px',
    lineHeight: '24px',
    fontWeight: 500,
    textAlign: 'left',
    letterSpacing: 0.3,
  },

  boldText: {
    fontWeight: 700,
  },

  container: {
    background: palette.white[0],
    display: 'flex',
    flexDirection: 'column',
    margin: '50px 190px 50px 190px',

    '@media (max-width: 1024px)': {
      margin: '50px 50px 50px 50px',
    },

    '@media (max-width: 479px)': {
      margin: '50px 25px 20px 25px',
    },
  },

  contentTitle: {
    color: palette.black[0],
    fontWeight: 700,
    fontSize: '25px',
    textAlign: 'center',
    letterSpacing: 0.3,
    margin: '0 0 10px 0',

    '@media (max-width: 479px)': {
      fontSize: '22px',
    },
  },

  contentSubTitle: {
    color: palette.black[0],
    fontWeight: 400,
    fontSize: '18px',
    textAlign: 'center',
    letterSpacing: 0.3,
    margin: '5px 0 10px 0',

    '@media (max-width: 479px)': {
      fontSize: '16px',
    },
  },

  indentedText: {
    margin: '0 0 0 20px'
  }
})

class Terms extends Component {
  startedVerification = (event) => {
    event.preventDefault()

    this.props.goToVerification()
  }

  render() {
    const { classes } = this.props

    return (
      <div className={classes.container}>
        <h1 className={classes.contentTitle}>Satstreet</h1>
        <h1 className={classes.contentTitle}>Terms of Use</h1>
        <h1 className={classes.contentSubTitle}>Last Modified: December 15, 2019</h1>
        <p className={classes.body}>

        <span className={classes.boldText}>1. Acceptance</span><br /><br />These terms and conditions (the “Terms”) are entered into by and between You and Satstreet Inc. (“Company”, “Satstreet”, “we” “us” or “our”). These Terms govern your access to and use of Satstreet.com including any content, functionality and services (the “Services”) offered on the Satstreet website and mobile application (collectively referred to as the “App”). By accessing and using our Services, you agree to be bound by these Terms and our Privacy Policy found at Satstreet.com/privacy, incorporated by reference link. We encourage you to read these Terms carefully before you start to use the App. If you do not agree to any of the provisions of these Terms and Privacy Policy you should immediately stop using the App.<br /><br />
        <span className={classes.boldText}>2. Definitions</span>For the purpose of these Terms:<br /><br />

        <span className={classes.boldText}>“AML” </span>stands for Anti-Money Laundering, which means a set of procedures, laws, and regulations that are intended to stop the practice of generating income through illegal actions.<br /><br />
        <span className={classes.boldText}>"Contributions" </span>means any unsolicited idea, suggestion or other material in any format;<br /><br />
        <span className={classes.boldText}>“Digital Asset(s)” </span>means digital representation(s) of value in which cryptographic protocols are used to regulate the generation and transaction of digital units.<br /><br />
        <span className={classes.boldText}>"Satstreet", "we", "us, or "our" </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        <span className={classes.boldText}>“AML” </span><br /><br />
        </p>
      </div>  
    )
  }
}

Terms.propTypes = {
  classes: PropTypes.object.isRequired
}

export default withStyles(styles)(Terms)
