import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Button from '@material-ui/core/Button'
import Dialog from '@material-ui/core/Dialog'
import Paper from '@material-ui/core/Paper'
import TextField from '@material-ui/core/TextField'
import Typography from '@material-ui/core/Typography'
import { palette } from '../../constants/styles'
import { withStyles } from '@material-ui/core/styles'
import Chart from './Chart'

var QRCode = require('qrcode.react')

const styles = {
  address: {
    fontSize: '13px'
  },

  container: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
    width: '100%',

    '@media (min-width:780px)': {
      width: '80%',
    },
  },

  contentContainer: {
    display: 'flex',
    justifyContent: 'center',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '40px 20px 40px 20px',

    '@media (min-width:780px)': {
      padding: '40px 40px 40px 40px',
    },
  },

  dialogContent: {
    padding: '15px 20px 20px 20px'
  },

  dialogTitleContainer: {
    alignItems: 'center',
    display: 'flex',
    flexDirection: 'row',
    fontSize: '17px',
    fontWeight: 700,
    height: '40px',
    justifyContent: 'space-between',
    padding: '0 5px 0 5px'
  },

  link: {
    color: palette.blue[0],
    fontWeight: 700,
    fontSize: '13px',
    textDecoration: 'none',
    margin: '5px 0 0 0',

    "&:hover": {
      textDecoration: 'underline'
    }
  },

  paperOptions: {
    boxShadow: 'none',
    borderRadius: '5px',
    margin: '30px 0 0 0',
    minHeight: '500px',
    minWidth: '300px',
    width: '100%',

    '@media (min-width:780px)': {
      border: '1px solid',
      borderColor: palette.gray[0],
      margin: '10vh 0 10vh 0',
      width: '30%'
    },
  },

  paperChart: {
    boxShadow: 'none',
    borderRadius: '5px',
    minHeight: '500px',
    margin: '30px 0 80px 0',
    width: '100%',

    '@media (min-width:780px)': {
      border: '1px solid',
      borderColor: palette.gray[0],
      margin: '10vh 0 10vh 0',
      width: '50%'
    },

    '@media (min-width:900px)': {
      width: '55%'
    },

    '@media (min-width:1000px)': {
      width: '60%'
    },

    '@media (min-width:1200px)': {
      width: '65%'
    },
  },

  qrButtonContainer: {
    alignItems: 'center',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-around',
    padding: '40px 20px 0 20px',
    maxWidth: '400px',
    width: '100%'
  },

  qrButton: {
    height: '50px',
    margin: '0 0 15px 0',
    width: '100%'
  },

  qrCode: {
    margin: '60px 0 20px 0'
  },

  textField: {
    marginTop: '25px'
  }
}

class Wallet extends Component {
  state = {
    email: '',
    emailHelperText: '',
    emailError: false,
    amount: '',
    amountHelperText: '',
    amountError: false,
    sendDialogIsOpen: true
  }

  handleChange = name => event => {
    event.preventDefault()

    this.setState({
      [name]: event.target.value,
      emailHelperText: '',
      emailError: false,
      amountHelperText: '',
      amountError: false
    })
  }

  handleSendDialogClose = () => {
    this.setState({ sendDialogIsOpen: false })
  }

  render() {
    const { email, emailHelperText, emailError, amount, amountHelperText, amountError, sendDialogIsOpen } = this.props
    const { classes } = this.props

    return (
      <div className={classes.container}>
        <Paper className={classes.paperOptions}>
          <div className={classes.contentContainer}>
            <Typography variant="h4" gutterBottom>
              0 Sats
            </Typography>
            0 BTC - $0.00

            <QRCode className={classes.qrCode} color={palette.blue[0]} size={160} value="1P4enaLERffNRpWcHqn5onmYDYZu4hr4p9" />
            <div className={classes.address}>1P4enaLERffNRpWcHqn5onmYDYZu4hr4p9</div>
            <a className={classes.link} href={"#"} onClick={this.sendEmail}>Copy</a>
            <div className={classes.qrButtonContainer}>
              <Button className={classes.qrButton} size="small" variant={'contained'} color="primary" onClick={this.handleOnClick}>
                Send
                </Button>
              <Button className={classes.qrButton} size="small" variant={'contained'} color="secondary" onClick={this.handleOnClick}>
                View Transactions
                </Button>
            </div>
          </div>
        </Paper>

        <Paper className={classes.paperChart}>
          <Chart />
        </Paper>

        <Dialog onClose={this.handleSendDialogClose} open={true}>
          <div className={classes.dialogContent}>
            <div className={classes.dialogTitleContainer}>
              Send
              <div />
            </div>
            <TextField
              fullWidth
              error={emailError}
              className={classes.textField}
              id="outlined-email"
              label="Email"
              name="email"
              type="email"
              helperText={emailHelperText}
              value={email}
              onChange={this.handleChange('email')}
              variant="outlined"
            />

            <TextField
              fullWidth
              error={amountError}
              className={classes.textField}
              id="outlined-amount"
              label="Amount"
              name="amount"
              type="number"
              helperText={amountHelperText}
              value={amount}
              onChange={this.handleChange('amount')}
              variant="outlined"
            />

            <Button
              className={classes.button}
              size="large"
              variant={'contained'}
              color="primary"
              fullWidth
              onClick={this.handleOnClick}
            >
              Email 
            </Button>
          </div>

        </Dialog>

      </div>
        )
      }
    }

Wallet.propTypes = {
          classes: PropTypes.object.isRequired
      }

export default withStyles(styles)(Wallet)
